import merge from 'lodash/merge'
import get from 'lodash/get'
import set from 'lodash/set'

const textProps = [
    'axis.ticks.text',
    'axis.legend.text',
    'legends.title.text',
    'legends.text',
    'legends.ticks.text',
    'legends.title.text',
    'labels.text',
    'dots.text',
    'markers.text',
    'annotations.text',
]

/**
 * @param {ThemeWithoutInheritance} defaultTheme
 * @param {Theme} customTheme
 * @returns {CompleteTheme}
 */
export const extendDefaultTheme = (defaultTheme, customTheme) => {
    const theme = merge({}, defaultTheme, customTheme)

    textProps.forEach(prop => {
        if (get(theme, `${prop}.fontFamily`) === undefined) {
            set(theme, `${prop}.fontFamily`, theme.text.fontFamily)
        }
        if (get(theme, `${prop}.fontSize`) === undefined) {
            set(theme, `${prop}.fontSize`, theme.text.fontSize)
        }
        if (get(theme, `${prop}.fill`) === undefined) {
            set(theme, `${prop}.fill`, theme.text.fill)
        }
        if (get(theme, `${prop}.outlineWidth`) === undefined) {
            set(theme, `${prop}.outlineWidth`, theme.text.outlineWidth)
        }
        if (get(theme, `${prop}.outlineColor`) === undefined) {
            set(theme, `${prop}.outlineColor`, theme.text.outlineColor)
        }
    })

    return theme
}
import { createContext, useMemo } from 'react'
import isString from 'lodash/isString'
import PropTypes from 'prop-types'
import { config as presets } from '@react-spring/web'

export const motionConfigContext = createContext()

export const motionDefaultProps = {
    animate: true,
    config: 'default',
}

export const MotionConfigProvider = props => {
    const { children, animate = true, config = 'default' } = props

    const value = useMemo(() => {
        const reactSpringConfig = isString(config) ? presets[config] : config

        return {
            animate,
            config: reactSpringConfig,
        }
    }, [animate, config])

    return <motionConfigContext.Provider value={value}>{children}</motionConfigContext.Provider>
}

export const motionPropTypes = {
    animate: PropTypes.bool,
    motionConfig: PropTypes.oneOfType([
        PropTypes.oneOf(Object.keys(presets)),
        PropTypes.shape({
            mass: PropTypes.number,
            tension: PropTypes.number,
            friction: PropTypes.number,
            clamp: PropTypes.bool,
            precision: PropTypes.number,
            velocity: PropTypes.number,
            duration: PropTypes.number,
            easing: PropTypes.func,
        }),
    ]),
}

MotionConfigProvider.propTypes = {
    children: PropTypes.node.isRequired,
    animate: motionPropTypes.animate,
    config: motionPropTypes.motionConfig,
}
