import { SymbolProps } from './types'

export const SymbolCircle = ({
    x,
    y,
    size,
    fill,
    opacity = 1,
    borderWidth = 0,
    borderColor = 'transparent',
}: SymbolProps) => {
    return (
        <circle
            r={size / 2}
            cx={x + size / 2}
            cy={y + size / 2}
            fill={fill}
            opacity={opacity}
            strokeWidth={borderWidth}
            stroke={borderColor}
            style={{
                pointerEvents: 'none',
            }}
        />
    )
}
import { SymbolProps } from './types'

export const SymbolDiamond = ({
    x,
    y,
    size,
    fill,
    opacity = 1,
    borderWidth = 0,
    borderColor = 'transparent',
}: SymbolProps) => {
    return (
        <g transform={`translate(${x},${y})`}>
            <path
                d={`
                    M${size / 2} 0
                    L${size * 0.8} ${size / 2}
                    L${size / 2} ${size}
                    L${size * 0.2} ${size / 2}
                    L${size / 2} 0
                `}
                fill={fill}
                opacity={opacity}
                strokeWidth={borderWidth}
                stroke={borderColor}
                style={{
                    pointerEvents: 'none',
                }}
            />
        </g>
    )
}
import { SymbolProps } from './types'

export const SymbolSquare = ({
    x,
    y,
    size,
    fill,
    opacity = 1,
    borderWidth = 0,
    borderColor = 'transparent',
}: SymbolProps) => {
    return (
        <rect
            x={x}
            y={y}
            fill={fill}
            opacity={opacity}
            strokeWidth={borderWidth}
            stroke={borderColor}
            width={size}
            height={size}
            style={{
                pointerEvents: 'none',
            }}
        />
    )
}
import { SymbolProps } from './types'

export const SymbolTriangle = ({
    x,
    y,
    size,
    fill,
    opacity = 1,
    borderWidth = 0,
    borderColor = 'transparent',
}: SymbolProps) => {
    return (
        <g transform={`translate(${x},${y})`}>
            <path
                d={`
                M${size / 2} 0
                L${size} ${size}
                L0 ${size}
                L${size / 2} 0
            `}
                fill={fill}
                opacity={opacity}
                strokeWidth={borderWidth}
                stroke={borderColor}
                style={{
                    pointerEvents: 'none',
                }}
            />
        </g>
    )
}
import { ContinuousColorsLegendProps } from './types'

export const continuousColorsLegendDefaults: {
    length: NonNullable<ContinuousColorsLegendProps['length']>
    thickness: NonNullable<ContinuousColorsLegendProps['thickness']>
    direction: NonNullable<ContinuousColorsLegendProps['direction']>
    tickPosition: NonNullable<ContinuousColorsLegendProps['tickPosition']>
    tickSize: NonNullable<ContinuousColorsLegendProps['tickSize']>
    tickSpacing: NonNullable<ContinuousColorsLegendProps['tickSpacing']>
    tickOverlap: NonNullable<ContinuousColorsLegendProps['tickOverlap']>
    tickFormat: NonNullable<ContinuousColorsLegendProps['tickFormat']>
    titleAlign: NonNullable<ContinuousColorsLegendProps['titleAlign']>
    titleOffset: NonNullable<ContinuousColorsLegendProps['titleOffset']>
} = {
    length: 200,
    thickness: 16,
    direction: 'row',
    tickPosition: 'after',
    tickSize: 4,
    tickSpacing: 3,
    tickOverlap: false,
    tickFormat: (value: number) => `${value}`,
    titleAlign: 'start',
    titleOffset: 4,
}
import { scaleLinear } from 'd3-scale'
import { getValueFormatter } from '@nivo/core'
import { computeContinuousColorScaleColorStops } from '@nivo/colors'
import {
    BoxLegendSvgProps,
    ContinuousColorsLegendProps,
    LegendAnchor,
    LegendItemDirection,
} from './types'
import { continuousColorsLegendDefaults } from './defaults'

const isObject = <T>(item: unknown): item is T =>
    typeof item === 'object' && !Array.isArray(item) && item !== null

const zeroPadding = {
    top: 0,
    right: 0,
    bottom: 0,
    left: 0,
}

export const computeDimensions = ({
    direction,
    itemsSpacing,
    padding: _padding,
    itemCount,
    itemWidth,
    itemHeight,
}: Pick<BoxLegendSvgProps, 'direction' | 'padding'> &
    Record<'itemsSpacing' | 'itemCount' | 'itemWidth' | 'itemHeight', number>) => {
    if (typeof _padding !== 'number' && !isObject(_padding)) {
        throw new Error('Invalid property padding, must be one of: number, object')
    }

    const padding =
        typeof _padding === 'number'
            ? {
                  top: _padding,
                  right: _padding,
                  bottom: _padding,
                  left: _padding,
              }
            : {
                  ...zeroPadding,
                  ..._padding,
              }

    const horizontalPadding = padding.left + padding.right
    const verticalPadding = padding.top + padding.bottom
    let width = itemWidth + horizontalPadding
    let height = itemHeight + verticalPadding
    const spacing = (itemCount - 1) * itemsSpacing
    if (direction === 'row') {
        width = itemWidth * itemCount + spacing + horizontalPadding
    } else if (direction === 'column') {
        height = itemHeight * itemCount + spacing + verticalPadding
    }

    return { width, height, padding }
}

export const computePositionFromAnchor = ({
    anchor,
    translateX,
    translateY,
    containerWidth,
    containerHeight,
    width,
    height,
}: { anchor: LegendAnchor } & Record<
    'translateX' | 'translateY' | 'containerWidth' | 'containerHeight' | 'width' | 'height',
    number
>) => {
    let x = translateX
    let y = translateY

    switch (anchor) {
        case 'top':
            x += (containerWidth - width) / 2
            break

        case 'top-right':
            x += containerWidth - width
            break

        case 'right':
            x += containerWidth - width
            y += (containerHeight - height) / 2
            break

        case 'bottom-right':
            x += containerWidth - width
            y += containerHeight - height
            break

        case 'bottom':
            x += (containerWidth - width) / 2
            y += containerHeight - height
            break

        case 'bottom-left':
            y += containerHeight - height
            break

        case 'left':
            y += (containerHeight - height) / 2
            break

        case 'center':
            x += (containerWidth - width) / 2
            y += (containerHeight - height) / 2
            break
    }

    return { x, y }
}

export const computeItemLayout = ({
    direction,
    justify,
    symbolSize,
    symbolSpacing,
    width,
    height,
}: {
    direction: LegendItemDirection
    justify: boolean
} & Record<'symbolSize' | 'symbolSpacing' | 'width' | 'height', number>) => {
    let symbolX
    let symbolY

    let labelX
    let labelY
    let labelAnchor: 'start' | 'middle' | 'end'
    let labelAlignment: 'alphabetic' | 'central' | 'text-before-edge'

    switch (direction) {
        case 'left-to-right':
            symbolX = 0
            symbolY = (height - symbolSize) / 2

            labelY = height / 2
            labelAlignment = 'central'
            if (justify) {
                labelX = width
                labelAnchor = 'end'
            } else {
                labelX = symbolSize + symbolSpacing
                labelAnchor = 'start'
            }
            break

        case 'right-to-left':
            symbolX = width - symbolSize
            symbolY = (height - symbolSize) / 2

            labelY = height / 2
            labelAlignment = 'central'
            if (justify) {
                labelX = 0
                labelAnchor = 'start'
            } else {
                labelX = width - symbolSize - symbolSpacing
                labelAnchor = 'end'
            }
            break

        case 'top-to-bottom':
            symbolX = (width - symbolSize) / 2
            symbolY = 0

            labelX = width / 2

            labelAnchor = 'middle'
            if (justify) {
                labelY = height
                labelAlignment = 'alphabetic'
            } else {
                labelY = symbolSize + symbolSpacing
                labelAlignment = 'text-before-edge'
            }
            break

        case 'bottom-to-top':
            symbolX = (width - symbolSize) / 2
            symbolY = height - symbolSize

            labelX = width / 2
            labelAnchor = 'middle'
            if (justify) {
                labelY = 0
                labelAlignment = 'text-before-edge'
            } else {
                labelY = height - symbolSize - symbolSpacing
                labelAlignment = 'alphabetic'
            }
            break
    }

    return {
        symbolX,
        symbolY,
        labelX,
        labelY,
        labelAnchor,
        labelAlignment,
    }
}

export const computeContinuousColorsLegend = ({
    scale,
    ticks,
    length = continuousColorsLegendDefaults.length,
    thickness = continuousColorsLegendDefaults.thickness,
    direction = continuousColorsLegendDefaults.direction,
    tickPosition = continuousColorsLegendDefaults.tickPosition,
    tickSize = continuousColorsLegendDefaults.tickSize,
    tickSpacing = continuousColorsLegendDefaults.tickSpacing,
    tickOverlap = continuousColorsLegendDefaults.tickOverlap,
    tickFormat = continuousColorsLegendDefaults.tickFormat,
    title,
    titleAlign = continuousColorsLegendDefaults.titleAlign,
    titleOffset = continuousColorsLegendDefaults.titleOffset,
}: ContinuousColorsLegendProps) => {
    // left to right for `row`, bottom to top for `column`
    const domain = direction === 'column' ? [...scale.domain()].reverse() : scale.domain()

    const positionScale = scaleLinear().domain(domain)
    if (domain.length === 2) {
        // sequential, quantize
        positionScale.range([0, length])
    } else if (domain.length === 3) {
        // diverging
        positionScale.range([0, length / 2, length])
    }

    let values: number[]
    if ('thresholds' in scale) {
        // quantize
        values = [domain[0], ...scale.thresholds(), domain[1]]
    } else {
        // sequential, diverging
        values = Array.isArray(ticks) ? ticks : (scale as any).ticks(ticks)
    }

    const colorStops = computeContinuousColorScaleColorStops(scale, 32)

    const formatValue = getValueFormatter(tickFormat)

    const computedTicks: {
        x1: number
        y1: number
        x2: number
        y2: number
        text: string
        textX: number
        textY: number
        textHorizontalAlign: 'start' | 'middle' | 'end'
        textVerticalAlign: 'alphabetic' | 'central' | 'hanging'
    }[] = []

    let width: number
    let height: number

    const gradientX1 = 0
    let gradientY1 = 0
    let gradientX2 = 0
    const gradientY2 = 0

    let titleX: number
    let titleY: number
    let titleRotation: number
    let titleVerticalAlign: 'alphabetic' | 'hanging'

    if (direction === 'row') {
        width = length
        height = thickness

        gradientX2 = 1

        let y1: number
        let y2: number

        let textY: number
        const textHorizontalAlign = 'middle'
        let textVerticalAlign: 'alphabetic' | 'hanging'

        titleRotation = 0
        if (titleAlign === 'start') {
            titleX = 0
        } else if (titleAlign === 'middle') {
            titleX = length / 2
        } else {
            titleX = length
        }

        if (tickPosition === 'before') {
            y1 = -tickSize
            y2 = tickOverlap ? thickness : 0

            textY = -tickSize - tickSpacing
            textVerticalAlign = 'alphabetic'

            titleY = thickness + titleOffset
            titleVerticalAlign = 'hanging'
        } else {
            y1 = tickOverlap ? 0 : thickness
            y2 = thickness + tickSize

            textY = y2 + tickSpacing
            textVerticalAlign = 'hanging'

            titleY = -titleOffset
            titleVerticalAlign = 'alphabetic'
        }

        values.forEach(value => {
            const x = positionScale(value)

            computedTicks.push({
                x1: x,
                y1,
                x2: x,
                y2,
                text: formatValue(value),
                textX: x,
                textY,
                textHorizontalAlign,
                textVerticalAlign,
            })
        })
    } else {
        width = thickness
        height = length

        gradientY1 = 1

        let x1: number
        let x2: number

        let textX: number
        let textHorizontalAlign: 'start' | 'end'
        const textVerticalAlign = 'central'

        titleRotation = -90
        if (titleAlign === 'start') {
            titleY = length
        } else if (titleAlign === 'middle') {
            titleY = length / 2
        } else {
            titleY = 0
        }

        if (tickPosition === 'before') {
            x1 = -tickSize
            x2 = tickOverlap ? thickness : 0

            textX = x1 - tickSpacing
            textHorizontalAlign = 'end'

            titleX = thickness + titleOffset
            titleVerticalAlign = 'hanging'
        } else {
            x1 = tickOverlap ? 0 : thickness
            x2 = thickness + tickSize

            textX = x2 + tickSpacing
            textHorizontalAlign = 'start'

            titleX = -titleOffset
            titleVerticalAlign = 'alphabetic'
        }

        values.forEach(value => {
            const y = positionScale(value)

            computedTicks.push({
                x1,
                y1: y,
                x2,
                y2: y,
                text: formatValue(value),
                textX,
                textY: y,
                textHorizontalAlign,
                textVerticalAlign,
            })
        })
    }

    return {
        width,
        height,
        gradientX1,
        gradientY1,
        gradientX2,
        gradientY2,
        colorStops,
        ticks: computedTicks,
        titleText: title,
        titleX,
        titleY,
        titleRotation,
        titleHorizontalAlign: titleAlign,
        titleVerticalAlign,
    }
}
import { Fragment } from 'react'
import { useTheme } from '@nivo/core'
import { computeContinuousColorsLegend } from '../compute'
import { ContinuousColorsLegendProps } from '../types'
import { continuousColorsLegendDefaults } from '../defaults'

export const ContinuousColorsLegendSvg = ({
    scale,
    ticks,
    length = continuousColorsLegendDefaults.length,
    thickness = continuousColorsLegendDefaults.thickness,
    direction = continuousColorsLegendDefaults.direction,
    tickPosition = continuousColorsLegendDefaults.tickPosition,
    tickSize = continuousColorsLegendDefaults.tickSize,
    tickSpacing = continuousColorsLegendDefaults.tickSpacing,
    tickOverlap = continuousColorsLegendDefaults.tickOverlap,
    tickFormat = continuousColorsLegendDefaults.tickFormat,
    title,
    titleAlign = continuousColorsLegendDefaults.titleAlign,
    titleOffset = continuousColorsLegendDefaults.titleOffset,
}: ContinuousColorsLegendProps) => {
    const {
        width,
        height,
        gradientX1,
        gradientY1,
        gradientX2,
        gradientY2,
        ticks: computedTicks,
        colorStops,
        titleText,
        titleX,
        titleY,
        titleRotation,
        titleVerticalAlign,
        titleHorizontalAlign,
    } = computeContinuousColorsLegend({
        scale,
        ticks,
        length,
        thickness,
        direction,
        tickPosition,
        tickSize,
        tickSpacing,
        tickOverlap,
        tickFormat,
        title,
        titleAlign,
        titleOffset,
    })

    const theme = useTheme()

    const id = `ContinuousColorsLegendSvgGradient.${direction}.${colorStops
        .map(stop => stop.offset)
        .join('_')}`

    return (
        <g>
            <defs>
                <linearGradient
                    id={id}
                    x1={gradientX1}
                    y1={gradientY1}
                    x2={gradientX2}
                    y2={gradientY2}
                >
                    {colorStops.map(colorStop => (
                        <stop {...colorStop} />
                    ))}
                </linearGradient>
            </defs>
            {titleText && (
                <text
                    transform={`translate(${titleX}, ${titleY}) rotate(${titleRotation})`}
                    textAnchor={titleHorizontalAlign}
                    dominantBaseline={titleVerticalAlign}
                    style={theme.legends.title.text}
                >
                    {titleText}
                </text>
            )}
            <rect width={width} height={height} fill={`url(#${id}`} />
            {computedTicks.map((tick, index) => (
                <Fragment key={index}>
                    <line
                        x1={tick.x1}
                        y1={tick.y1}
                        x2={tick.x2}
                        y2={tick.y2}
                        style={theme.legends.ticks.line}
                    />
                    <text
                        x={tick.textX}
                        y={tick.textY}
                        textAnchor={tick.textHorizontalAlign}
                        dominantBaseline={tick.textVerticalAlign}
                        style={theme.legends.ticks.text}
                    >
                        {tick.text}
                    </text>
                </Fragment>
            ))}
        </g>
    )
}
import { AnchoredContinuousColorsLegendProps } from '../types'
import { computePositionFromAnchor } from '../compute'
import { continuousColorsLegendDefaults } from '../defaults'
import { ContinuousColorsLegendSvg } from './ContinuousColorsLegendSvg'

export const AnchoredContinuousColorsLegendSvg = ({
    containerWidth,
    containerHeight,
    anchor,
    translateX = 0,
    translateY = 0,
    length = continuousColorsLegendDefaults.length,
    thickness = continuousColorsLegendDefaults.thickness,
    direction = continuousColorsLegendDefaults.direction,
    ...legendProps
}: AnchoredContinuousColorsLegendProps) => {
    let width: number
    let height: number
    if (direction === 'row') {
        width = length
        height = thickness
    } else {
        width = thickness
        height = length
    }

    const { x, y } = computePositionFromAnchor({
        anchor,
        translateX,
        translateY,
        containerWidth,
        containerHeight,
        width,
        height,
    })

    return (
        <g transform={`translate(${x}, ${y})`}>
            <ContinuousColorsLegendSvg
                length={length}
                thickness={thickness}
                direction={direction}
                {...legendProps}
            />
        </g>
    )
}
import { useState, useCallback } from 'react'
import * as React from 'react'
import { useTheme } from '@nivo/core'
import { LegendSvgItemProps } from '../types'
import { computeItemLayout } from '../compute'
import { SymbolCircle, SymbolDiamond, SymbolSquare, SymbolTriangle } from './symbols'

type Style = Partial<{
    itemBackground: string
    itemOpacity: number
    itemTextColor: string
    symbolBorderColor: string
    symbolBorderWidth: number
    symbolSize: number
}>

const symbolByShape = {
    circle: SymbolCircle,
    diamond: SymbolDiamond,
    square: SymbolSquare,
    triangle: SymbolTriangle,
}

export const LegendSvgItem = ({
    x,
    y,
    width,
    height,
    data,
    direction = 'left-to-right',
    justify = false,
    textColor,
    background = 'transparent',
    opacity = 1,

    symbolShape = 'square',
    symbolSize = 16,
    symbolSpacing = 8,
    symbolBorderWidth = 0,
    symbolBorderColor = 'transparent',

    onClick,
    onMouseEnter,
    onMouseLeave,
    toggleSerie,

    effects,
}: LegendSvgItemProps) => {
    const [style, setStyle] = useState<Style>({})
    const theme = useTheme()

    const handleMouseEnter = useCallback(
        (event: React.MouseEvent<SVGRectElement>) => {
            if (effects) {
                const applyEffects = effects.filter(({ on }) => on === 'hover')
                const style = applyEffects.reduce(
                    (acc, effect) => ({
                        ...acc,
                        ...effect.style,
                    }),
                    {}
                )
                setStyle(style)
            }

            onMouseEnter?.(data, event)
        },
        [onMouseEnter, data, effects]
    )
    const handleMouseLeave = useCallback(
        (event: React.MouseEvent<SVGRectElement>) => {
            if (effects) {
                const applyEffects = effects.filter(({ on }) => on !== 'hover')
                const style = applyEffects.reduce(
                    (acc, effect) => ({
                        ...acc,
                        ...effect.style,
                    }),
                    {}
                )
                setStyle(style)
            }

            onMouseLeave?.(data, event)
        },
        [onMouseLeave, data, effects]
    )

    const { symbolX, symbolY, labelX, labelY, labelAnchor, labelAlignment } = computeItemLayout({
        direction,
        justify,
        symbolSize: style.symbolSize ?? symbolSize,
        symbolSpacing,
        width,
        height,
    })

    const isInteractive = [onClick, onMouseEnter, onMouseLeave, toggleSerie].some(
        handler => handler !== undefined
    )

    const SymbolShape = typeof symbolShape === 'function' ? symbolShape : symbolByShape[symbolShape]

    return (
        <g
            transform={`translate(${x},${y})`}
            style={{
                opacity: style.itemOpacity ?? opacity,
            }}
        >
            <rect
                width={width}
                height={height}
                fill={style.itemBackground ?? background}
                style={{
                    cursor: isInteractive ? 'pointer' : 'auto',
                }}
                onClick={event => {
                    onClick?.(data, event)
                    toggleSerie?.(data.id)
                }}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
            />
            {React.createElement(SymbolShape, {
                id: data.id,
                x: symbolX,
                y: symbolY,
                size: style.symbolSize ?? symbolSize,
                fill: data.fill ?? data.color ?? 'black',
                borderWidth: style.symbolBorderWidth ?? symbolBorderWidth,
                borderColor: style.symbolBorderColor ?? symbolBorderColor,
                ...(data.hidden ? theme.legends.hidden.symbol : undefined),
            })}
            <text
                textAnchor={labelAnchor}
                style={{
                    ...theme.legends.text,
                    fill: style.itemTextColor ?? textColor ?? theme.legends.text.fill ?? 'black',
                    dominantBaseline: labelAlignment,
                    pointerEvents: 'none',
                    userSelect: 'none',
                    ...(data.hidden ? theme.legends.hidden.text : undefined),
                }}
                x={labelX}
                y={labelY}
            >
                {data.label}
            </text>
        </g>
    )
}
import { LegendSvgItem } from './LegendSvgItem'
import { LegendSvgProps } from '../types'
import { computeDimensions } from '../compute'

export const LegendSvg = ({
    data,

    x,
    y,
    direction,
    padding: _padding = 0,
    justify,
    effects,

    itemWidth,
    itemHeight,
    itemDirection = 'left-to-right',
    itemsSpacing = 0,
    itemTextColor,
    itemBackground = 'transparent',
    itemOpacity = 1,

    symbolShape,
    symbolSize,
    symbolSpacing,
    symbolBorderWidth,
    symbolBorderColor,

    onClick,
    onMouseEnter,
    onMouseLeave,
    toggleSerie,
}: LegendSvgProps) => {
    const { padding } = computeDimensions({
        itemCount: data.length,
        itemWidth,
        itemHeight,
        itemsSpacing,
        direction,
        padding: _padding,
    })

    const xStep = direction === 'row' ? itemWidth + itemsSpacing : 0
    const yStep = direction === 'column' ? itemHeight + itemsSpacing : 0

    return (
        <g transform={`translate(${x},${y})`}>
            {data.map((data, i) => (
                <LegendSvgItem
                    key={i}
                    data={data}
                    x={i * xStep + padding.left}
                    y={i * yStep + padding.top}
                    width={itemWidth}
                    height={itemHeight}
                    direction={itemDirection}
                    justify={justify}
                    effects={effects}
                    textColor={itemTextColor}
                    background={itemBackground}
                    opacity={itemOpacity}
                    symbolShape={symbolShape}
                    symbolSize={symbolSize}
                    symbolSpacing={symbolSpacing}
                    symbolBorderWidth={symbolBorderWidth}
                    symbolBorderColor={symbolBorderColor}
                    onClick={onClick}
                    onMouseEnter={onMouseEnter}
                    onMouseLeave={onMouseLeave}
                    toggleSerie={toggleSerie}
                />
            ))}
        </g>
    )
}
import { LegendSvg } from './LegendSvg'
import { BoxLegendSvgProps } from '../types'
import { computeDimensions, computePositionFromAnchor } from '../compute'

export const BoxLegendSvg = ({
    data,

    containerWidth,
    containerHeight,
    translateX = 0,
    translateY = 0,
    anchor,
    direction,
    padding = 0,
    justify,

    itemsSpacing = 0,
    itemWidth,
    itemHeight,
    itemDirection,
    itemTextColor,
    itemBackground,
    itemOpacity,

    symbolShape,
    symbolSize,
    symbolSpacing,
    symbolBorderWidth,
    symbolBorderColor,

    onClick,
    onMouseEnter,
    onMouseLeave,
    toggleSerie,

    effects,
}: BoxLegendSvgProps) => {
    const { width, height } = computeDimensions({
        itemCount: data.length,
        itemsSpacing,
        itemWidth,
        itemHeight,
        direction,
        padding,
    })

    const { x, y } = computePositionFromAnchor({
        anchor,
        translateX,
        translateY,
        containerWidth,
        containerHeight,
        width,
        height,
    })

    return (
        <LegendSvg
            data={data}
            x={x}
            y={y}
            direction={direction}
            padding={padding}
            justify={justify}
            effects={effects}
            itemsSpacing={itemsSpacing}
            itemWidth={itemWidth}
            itemHeight={itemHeight}
            itemDirection={itemDirection}
            itemTextColor={itemTextColor}
            itemBackground={itemBackground}
            itemOpacity={itemOpacity}
            symbolShape={symbolShape}
            symbolSize={symbolSize}
            symbolSpacing={symbolSpacing}
            symbolBorderWidth={symbolBorderWidth}
            symbolBorderColor={symbolBorderColor}
            onClick={onClick}
            onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}
            toggleSerie={typeof toggleSerie === 'boolean' ? undefined : toggleSerie}
        />
    )
}
import { CompleteTheme, degreesToRadians } from '@nivo/core'
import {
    computeDimensions,
    computePositionFromAnchor,
    computeItemLayout,
    computeContinuousColorsLegend,
} from './compute'
import { AnchoredContinuousColorsLegendProps, LegendCanvasProps } from './types'
import { continuousColorsLegendDefaults } from './defaults'

const textAlignMapping = {
    start: 'left',
    middle: 'center',
    end: 'right',
} as const

export const renderLegendToCanvas = (
    ctx: CanvasRenderingContext2D,
    {
        data,

        containerWidth,
        containerHeight,
        translateX = 0,
        translateY = 0,
        anchor,
        direction,
        padding: _padding = 0,
        justify = false,

        // items
        itemsSpacing = 0,
        itemWidth,
        itemHeight,
        itemDirection = 'left-to-right',
        itemTextColor,

        // symbol
        symbolSize = 16,
        symbolSpacing = 8,
        // @todo add support for shapes
        // symbolShape = LegendSvgItem.defaultProps.symbolShape,

        theme,
    }: LegendCanvasProps
) => {
    const { width, height, padding } = computeDimensions({
        itemCount: data.length,
        itemWidth,
        itemHeight,
        itemsSpacing,
        direction,
        padding: _padding,
    })

    const { x, y } = computePositionFromAnchor({
        anchor,
        translateX,
        translateY,
        containerWidth,
        containerHeight,
        width,
        height,
    })

    const xStep = direction === 'row' ? itemWidth + itemsSpacing : 0
    const yStep = direction === 'column' ? itemHeight + itemsSpacing : 0

    ctx.save()
    ctx.translate(x, y)

    ctx.font = `${theme.legends.text.fontSize}px ${theme.legends.text.fontFamily || 'sans-serif'}`

    data.forEach((d, i) => {
        const itemX = i * xStep + padding.left
        const itemY = i * yStep + padding.top

        const { symbolX, symbolY, labelX, labelY, labelAnchor, labelAlignment } = computeItemLayout(
            {
                direction: itemDirection,
                justify,
                symbolSize,
                symbolSpacing,
                width: itemWidth,
                height: itemHeight,
            }
        )

        ctx.fillStyle = d.color ?? 'black'
        ctx.fillRect(itemX + symbolX, itemY + symbolY, symbolSize, symbolSize)

        ctx.textAlign = textAlignMapping[labelAnchor]

        if (labelAlignment === 'central') {
            ctx.textBaseline = 'middle'
        }

        ctx.fillStyle = itemTextColor ?? theme.legends.text.fill ?? 'black'
        ctx.fillText(String(d.label), itemX + labelX, itemY + labelY)
    })

    ctx.restore()
}

export const renderContinuousColorLegendToCanvas = (
    ctx: CanvasRenderingContext2D,
    {
        containerWidth,
        containerHeight,
        anchor,
        translateX = 0,
        translateY = 0,
        scale,
        length = continuousColorsLegendDefaults.length,
        thickness = continuousColorsLegendDefaults.thickness,
        direction = continuousColorsLegendDefaults.direction,
        ticks: _ticks,
        tickPosition = continuousColorsLegendDefaults.tickPosition,
        tickSize = continuousColorsLegendDefaults.tickSize,
        tickSpacing = continuousColorsLegendDefaults.tickSpacing,
        tickOverlap = continuousColorsLegendDefaults.tickOverlap,
        tickFormat = continuousColorsLegendDefaults.tickFormat,
        title,
        titleAlign = continuousColorsLegendDefaults.titleAlign,
        titleOffset = continuousColorsLegendDefaults.titleOffset,
        theme,
    }: AnchoredContinuousColorsLegendProps & {
        theme: CompleteTheme
    }
) => {
    const {
        width,
        height,
        gradientX1,
        gradientY1,
        gradientX2,
        gradientY2,
        colorStops,
        ticks,
        titleText,
        titleX,
        titleY,
        titleRotation,
        titleVerticalAlign,
        titleHorizontalAlign,
    } = computeContinuousColorsLegend({
        scale,
        ticks: _ticks,
        length,
        thickness,
        direction,
        tickPosition,
        tickSize,
        tickSpacing,
        tickOverlap,
        tickFormat,
        title,
        titleAlign,
        titleOffset,
    })

    const { x, y } = computePositionFromAnchor({
        anchor,
        translateX,
        translateY,
        containerWidth,
        containerHeight,
        width,
        height,
    })

    const initialStyles = {
        font: ctx.font,
        textAlign: ctx.textAlign,
        textBaseline: ctx.textBaseline,
    }
    ctx.save()

    ctx.translate(x, y)

    const gradient = ctx.createLinearGradient(
        gradientX1 * width,
        gradientY1 * height,
        gradientX2 * width,
        gradientY2 * height
    )
    colorStops.forEach(colorStop => {
        gradient.addColorStop(colorStop.offset, colorStop.stopColor)
    })

    ctx.fillStyle = gradient
    ctx.fillRect(0, 0, width, height)

    ctx.font = `${
        theme.legends.ticks.text.fontWeight ? `${theme.legends.ticks.text.fontWeight} ` : ''
    }${theme.legends.ticks.text.fontSize}px ${theme.legends.ticks.text.fontFamily}`

    ticks.forEach(tick => {
        if ((theme.legends.ticks.line.strokeWidth ?? 0) > 0) {
            ctx.lineWidth = Number(theme.axis.ticks.line.strokeWidth)
            if (theme.axis.ticks.line.stroke) {
                ctx.strokeStyle = theme.axis.ticks.line.stroke
            }
            ctx.lineCap = 'square'

            ctx.beginPath()
            ctx.moveTo(tick.x1, tick.y1)
            ctx.lineTo(tick.x2, tick.y2)
            ctx.stroke()
        }

        if (theme.legends.ticks.text.fill) {
            ctx.fillStyle = theme.legends.ticks.text.fill
        }
        ctx.textAlign = tick.textHorizontalAlign === 'middle' ? 'center' : tick.textHorizontalAlign
        ctx.textBaseline = tick.textVerticalAlign === 'central' ? 'middle' : tick.textVerticalAlign

        ctx.fillText(tick.text, tick.textX, tick.textY)
    })

    if (titleText) {
        ctx.save()
        ctx.translate(titleX, titleY)
        ctx.rotate(degreesToRadians(titleRotation))

        ctx.font = `${
            theme.legends.title.text.fontWeight ? `${theme.legends.title.text.fontWeight} ` : ''
        }${theme.legends.title.text.fontSize}px ${theme.legends.title.text.fontFamily}`
        if (theme.legends.title.text.fill) {
            ctx.fillStyle = theme.legends.title.text.fill
        }
        ctx.textAlign = titleHorizontalAlign === 'middle' ? 'center' : titleHorizontalAlign
        ctx.textBaseline = titleVerticalAlign

        ctx.fillText(titleText, 0, 0)

        ctx.restore()
    }

    ctx.restore()

    ctx.font = initialStyles.font
    ctx.textAlign = initialStyles.textAlign
    ctx.textBaseline = initialStyles.textBaseline
}
import { useMemo } from 'react'

type Scale = {
    (value: number): number
    invertExtent: (value: number) => [number, number]
    range: () => number[]
}

export const useQuantizeColorScaleLegendData = ({
    scale,
    domain: overriddenDomain,
    reverse = false,
    valueFormat = v => v,
    separator = ' - ',
}: {
    scale: Scale
    domain?: number[]
    reverse?: boolean
    valueFormat?: <T, U>(value: T) => T | U
    separator?: string
}) => {
    return useMemo(() => {
        const domain = overriddenDomain ?? scale.range()

        const items = domain.map((domainValue, index) => {
            const [start, end] = scale.invertExtent(domainValue)

            return {
                id: domainValue,
                index,
                extent: [start, end],
                label: `${valueFormat(start)}${separator}${valueFormat(end)}`,
                value: scale(start),
                color: domainValue,
            }
        })

        if (reverse) items.reverse()

        return items
    }, [overriddenDomain, scale, reverse, separator, valueFormat])
}
import PropTypes from 'prop-types'

/**
 * The prop type is exported as a simple object instead of `PropTypes.shape`
 * to be able to add extra properties.
 *
 * @example
 * ```javascript
 * import { LegendPropShape } from '@nivo/legends'
 *
 * const customLegendPropType = PropTypes.shape({
 *     ...LegendPropShape,
 *     extra: PropTypes.any.isRequired,
 * })
 * ```
 */
export const LegendPropShape = {
    data: PropTypes.arrayOf(PropTypes.object),

    // position & layout
    anchor: PropTypes.oneOf([
        'top',
        'top-right',
        'right',
        'bottom-right',
        'bottom',
        'bottom-left',
        'left',
        'top-left',
        'center',
    ]).isRequired,
    translateX: PropTypes.number,
    translateY: PropTypes.number,
    direction: PropTypes.oneOf(['row', 'column']).isRequired,

    // item
    itemsSpacing: PropTypes.number,
    itemWidth: PropTypes.number.isRequired,
    itemHeight: PropTypes.number.isRequired,
    itemDirection: PropTypes.oneOf([
        'left-to-right',
        'right-to-left',
        'top-to-bottom',
        'bottom-to-top',
    ]),
    itemTextColor: PropTypes.string,
    itemBackground: PropTypes.string,
    itemOpacity: PropTypes.number,

    symbolShape: PropTypes.oneOfType([
        PropTypes.oneOf(['circle', 'diamond', 'square', 'triangle']),
        PropTypes.func,
    ]),
    symbolSize: PropTypes.number,
    symbolSpacing: PropTypes.number,
    symbolBorderWidth: PropTypes.number,
    symbolBorderColor: PropTypes.string,

    onClick: PropTypes.func,
    onMouseEnter: PropTypes.func,
    onMouseLeave: PropTypes.func,

    effects: PropTypes.arrayOf(
        PropTypes.shape({
            on: PropTypes.oneOfType([PropTypes.oneOf(['hover'])]).isRequired,
            style: PropTypes.shape({
                itemTextColor: PropTypes.string,
                itemBackground: PropTypes.string,
                itemOpacity: PropTypes.number,
                symbolSize: PropTypes.number,
                symbolBorderWidth: PropTypes.number,
                symbolBorderColor: PropTypes.string,
            }).isRequired,
        })
    ),
}
