import { ComputedSerieAxis, ScaleAxis, ScaleLog, ScaleLogSpec } from './types'

export const createLogScale = (
    { base = 10, min = 'auto', max = 'auto' }: ScaleLogSpec,
    data: ComputedSerieAxis<number>,
    size: number,
    axis: ScaleAxis
